// CTile.h/*	CTile is a subclass of LPane.	Overrides FinishCreateSelf, DrawSelf, ClickSelf, and SpendTime.	Has UpdateCaption and SetWinner. */#pragma once#include "LPane.h"#include "LPeriodical.h"#include "CTileView.h"class CTile : public LPane , public LPeriodical {public:	enum { class_ID = FOUR_CHAR_CODE('TPsa') };		CTile(LStream* inStream);		virtual ~CTile();		virtual void FinishCreateSelf();		virtual void DrawSelf();		virtual void ClickSelf(const SMouseDownEvent& inMouseDown);		virtual	void SpendTime(const EventRecord& inMacEvent);		virtual void UpdateCaption(LCaption* caption);		virtual void SetWinner(Boolean winnerState);	protected:		ResIDT mResID;		Str255 mTitle;		SndChannelPtr mSoundChannel;		Boolean mTall;		SInt32 mDeltaX, mDeltaY;		UInt32 mTickCount;		Boolean mWinner;		UInt32 mWinnerStart;	private:	void SetRandomVelocity();	};